package com.example.lab6java;import javafx.fxml.FXMLLoader;import javafx.scene.Scene;import javafx.scene.image.Image;import javafx.stage.Stage;import java.io.File;import java.io.IOException;public class Application extends javafx.application.Application {    @Override    public void start(Stage stage) throws IOException {        FXMLLoader fxmlLoader = new FXMLLoader(Application.class.getResource("ApplicationView.fxml"));        Scene scene = new Scene(fxmlLoader.load(), 300, 300);        stage.setTitle("cat!");        stage.getIcons().add(new Image("https://cdn-icons-png.flaticon.com/512/57/57162.png"));        stage.setResizable(false);        stage.setScene(scene);        stage.show();    }    public static void main(String[] args) {        launch();    }}